import React, { useState } from "react";
import "./App.scss";
import logo from "./images/logo_pineapple.png";
import pineapple from "./images/pineapple.png";
import arrow from "./images/ic_arrow.png";
import fb from "./images/Facebook.svg";

function App() {
  const [validationMessage, setValidationMessage] = useState("");
  const [checked, setChecked] = useState(false);

  const emailValidation = (e) => {
    let formIsValid = true;
    let value = document.forms["subscription"]["email"].value;

    let re =
      /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    let reDomain = /.co/;
    console.log(reDomain.test(value));

    if (re.test(value) === false) {
      setValidationMessage("Please provide a valid e-mail address");
      formIsValid = false;
    }
    if (!checked) {
      setValidationMessage("You must accept the terms and conditions");
      formIsValid = false;
    }

    if (!value) {
      setValidationMessage("Email address is required");
      formIsValid = false;
    }
    if (email.indexOf(".co", email.length - ".co".length) !== -1) {
      //VALID
      console.log("VALID");
    } else {
      setValidationMessage("");
      formIsValid = true;
    }

    return formIsValid;
  };

  //   Invalid email is added - “Please provide a valid e-mail address”
  // - The checkbox is not marked - “You must accept the terms and conditions”
  // - No email address is provided - “Email address is required”
  // - Provided email is ending with .co - “We are not accepting subscriptions from Colombia
  // emails”

  const emailSubmit = (e) => {
    e.preventDefault();

    if (emailValidation()) {
      console.log("Form submitted");
    } else {
      console.log("Form has errors.");
    }
  };

  return (
    <div className="App">
      <div className="split-screen">
        <div className="split-left">
          <header className="navbar">
            <div className="logo">
              <a href="/" className="logo-image">
                <img src={logo} alt="logo" />
              </a>
              <img className="logo-text" src={pineapple} alt="logo" />{" "}
            </div>
            <ul>
              <li>
                <a href="#">About</a>
              </li>
              <li>
                <a href="#">How it works</a>
              </li>
              <li>
                <a href="#">Contact</a>
              </li>
            </ul>
          </header>

          <main>
            <div className="heading">
              <h1 className="main-heading">Subscribe to newsletter</h1>
              <h3 className="sub-heading">
                Subscribe to our newsletter and get 10% discount on pineapple
                glasses.
              </h3>
            </div>
            <form
              onSubmit={emailSubmit}
              name="subscription"
              // method="post"
              // action="process.php"
            >
              <div className="input-group">
                <input
                  name="email"
                  placeholder="Type your email address here…"
                  type="text"
                />
                <button type="submit" className="btn">
                  <img src={arrow} alt="submit" onClick={emailValidation} />
                </button>
              </div>
              <p>{validationMessage}</p>
            </form>
            <div className="tos">
              <input
                onChange={() => setChecked(!checked)}
                checked={checked}
                className="tos-checkbox"
                type="checkbox"
                name="terms-of-service"
              ></input>
              <label htmlFor="tos" className="tos-label">
                I agree to <a href="/"> terms of service</a>
              </label>
            </div>
            <hr />
            <div className="social-icons">
              <a href="#">
                <img src={fb} alt="" />
              </a>
              <a href="#">
                <img src={fb} alt="" />
              </a>
              <a href="#">
                <img src={fb} alt="" />
              </a>
              <a href="#">
                <img src={fb} alt="" />
              </a>
            </div>
          </main>
        </div>
        <div className="split-right"></div>
      </div>
    </div>
  );
}

export default App;
