import React from "react";
import axios from "axios";
import { urlRead, urlCreate, urlDelete } from "../endpoints";

export default function Contacts() {
  const [data, setData] = React.useState([]);
  const [domainButtons, setDomainButtons] = React.useState([]);

  async function getData() {
    try {
      const response = await axios.get(urlRead);
      console.log(response);
      setData(response.data);
    } catch (err) {
      console.error(err);
    }
  }

  React.useEffect(() => {
    getData();
    uniqueDomains();
  }, []);

  const sortData = (e) => {
    const objs = [...data];
    const name = e.target.id;
    console.log(name);

    function compare(a, b) {
      if (a[name] < b[name]) {
        return -1;
      }
      if (a[name] > b[name]) {
        return 1;
      }
      return 0;
    }

    objs.sort(compare);
    setData(objs);
  };

  const uniqueDomains = () => {
    const unique = [...new Set(data.map((item) => item.domain))];
    setDomainButtons(unique);
  };

  return (
    <div>
      <button onClick={sortData}>TEST</button>
      <table>
        <thead>
          <tr>
            <th id="email" onClick={sortData}>
              email
              <button id="email" onClick={sortData}>
                sort
              </button>
            </th>
            <th id="domain" onClick={sortData}>
              domain{" "}
              <button id="domain" onClick={sortData}>
                sort
              </button>
            </th>
            <th id="date" onClick={sortData}>
              date{" "}
              <button id="date" onClick={sortData}>
                sort
              </button>
            </th>
          </tr>
        </thead>
        <tbody>
          {data.map((row) => {
            return (
              <tr key={row.id}>
                <th>{row.email}</th>
                <th>{row.domain}</th>
                <th>{row.date}</th>
              </tr>
            );
          })}
        </tbody>
        <tfoot></tfoot>
      </table>
    </div>
  );
}
